type ActionType = {
	name: string;
	description: string;
	toHit: number;
	damageDie: string;
	damageBonus: number;
};

export type MonsterType = {
	img: string;
	name: string;
	stats?: {
		str: number;
		dex: number;
		con: number;
		int: number;
		wis: number;
		cha: number;
	};
	hp: number;
	ac: number;
	speed: number;
	turn?: {
		actions: ActionType[];
		apa: number;
	};
};

export const mockMonsters: MonsterType[] = [
	{
		img: "https://images-wixmp-ed30a86b8c4ca887773594c2.wixmp.com/f/dc09f9a2-3d45-4e94-83a1-ed1dac7787a9/dextnx9-cd5babe1-d9ee-4cbf-a114-c5fdb40fe224.jpg?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..a3wJeTPtfOynXsgYOjMjyfQQuoS2jv2gYO0vA7lvzTA",
		name: "Orc Sniper",
		hp: 40,
		ac: 14,
		speed: 30,
		stats: {
			str: 15,
			dex: 16,
			con: 16,
			int: 7,
			wis: 10,
			cha: 10,
		},
		turn: {
			apa: 2,
			actions: [
				{
					name: "Shortsword",
					description: "Melee Attack slashing",
					toHit: 4,
					damageDie: "1d8",
					damageBonus: 2,
				},
				{
					name: "Shortbow",
					description:
						"Ranged Attack (80/320) piercing **Natural 16+: Extra 1d6 poison damage**",
					toHit: 4,
					damageDie: "1d6",
					damageBonus: 3,
				},
			],
		},
	},
];
