import React from "react";
import MainBody from "../../components/MainBody";
import { Container } from "@mui/material";
import Monster from "../../components/dnd/Monster";
import "./Encounters.css";
import { mockMonsters } from "../../mock/MockMonsters";
import { useRoll } from "../../assets/hooks";

function Encounters() {
	const monsters = mockMonsters;
	const { result, dice, modifier } = useRoll();
	return (
		<MainBody>
			<Container maxWidth="lg">
				<div className="monsters_list">
					{monsters.map((monster) => {
						return <Monster {...monster} />;
					})}
				</div>
			</Container>
			<div className="diceResult">
				<div className="diceResult__title">Roll</div>
				<div className="diceResult__diceAndMod">
					{dice} {modifier}
				</div>
				<div className="diceResult__results">{result}</div>
			</div>
		</MainBody>
	);
}

export default Encounters;
