import { useState } from "react";

export const useForm = (callback: any, initialState = {}) => {
	const [values, setValues] = useState(initialState);

	const onChange = (event: any) => {
		setValues({ ...values, [event.target.name]: event.target.value });
		console.log(values);
	};

	const onSubmit = (event: any) => {
		event.preventDefault();
		callback();
	};

	return {
		onChange,
		onSubmit,
		values,
	};
};

type ResultType = {
	dice: string;
	modifier: number;
	result: number;
};

export const useRoll = () => {
	const [results, setResults] = useState<ResultType[]>([]);
	const [dice, setDice] = useState("1d20");
	const [modifier, setModifier] = useState(0);

	const roll = () => {
		const result = rollDice(dice, modifier);
		setResults([...results, { dice, modifier, result }]);
	};

	return { results, dice, modifier, setDice, setModifier, roll };
};

const rollDice = (dice: string, modifier = 0) => {
	const [numDice, die] = dice.split("d");

	let total = 0;
	for (let i = 0; i < parseInt(numDice); i++) {
		total += Math.floor(Math.random() * parseInt(die)) + 1;
	}
	return total + modifier;
};
