import { TextField, Button, Container, Stack, Alert } from "@mui/material";
import MainBody from '../../components/MainBody';

const REGISTER_USER = gql`
  mutation Mutation(
    $registerInput: RegisterInput
  ) {
    registerUser(
      registerInput: $registerInput
    ) {
      username
      token
    }
  }
`

