import React, { useState } from "react";
import { MonsterType } from "../../mock/MockMonsters";
import { Button, TextField } from "@mui/material";
import "./Monster.css";
import { Context } from "../../assets/diceStore";

const Monster = (props: MonsterType) => {
	const [damageTaken, setDamageTaken] = useState(0);
	const { result, setResult } = React.useContext(Context);

	const updateDamage = (value: string) => {
		let calculatedDamage = 0;
		if (value.includes("-")) return;
		if (value.includes("+")) {
			value.split("+").forEach((val) => {
				calculatedDamage += parseInt(val);
			});
		} else {
			calculatedDamage = parseInt(value);
		}

		if (Number.isNaN(calculatedDamage)) return 0;
		setDamageTaken(calculatedDamage);
	};

	const calculateModifier = (stat: number | undefined) => {
		if (!stat || stat === 0) return -100;

		if (stat - 10 > 0) {
			return Math.floor((stat - 10) / 2);
		}
		if (stat - 10 < 0) {
			return Math.ceil((stat - 10) / 2);
		}
		return 0;
	};

	return (
		<div className="monster">
			<div className="monster__name">
				<h3>{props.name}</h3>
			</div>

			<div className="monster__container">
				{/* This is the image of the monster */}
				<div className="monster__img">
					<img src={props.img} alt={props.name} />
				</div>

				<div className="monster__description">
					{/* These are the main stats (at a glance) */}
					<div className="monster__mainStats">
						<div className="monster__hp bordered">
							<div className="monster__hp__name">HP</div>
							<div
								className={`monster__stat__value emphasized
                ${
									props.hp - damageTaken <= props.hp * 0.2
										? "bad"
										: props.hp - damageTaken >= props.hp * 0.7
										? "good"
										: ""
								}`}
							>
								{props.hp - damageTaken <= 0 ? "Dead" : props.hp - damageTaken}
							</div>
						</div>
						<div className="monster__ac">
							<div className="monster__ac__name">AC</div>
							<div className="monster__stat__value">{props.ac}</div>
						</div>
						<div className="monster__speed">
							<div className="monster__speed__name">Speed</div>
							<div className="monster__stat__value">{props.speed}ft</div>
						</div>
					</div>
					{/* This is the stat section */}
					<div className="monster__stats">
						<Button
							variant="outlined"
							className="monster__stat"
							onClick={() => {
								const modifier = calculateModifier(props.stats?.str);
								setResult({ ...result!, dice: "1d20", modifier: modifier });
							}}
						>
							<div className="monster__stat__name">STR</div>
							<div className="monster__stat__value">
								{props.stats ? props.stats.str : 0}
							</div>
						</Button>

						<Button
							variant="outlined"
							className="monster__stat"
							onClick={() => {
								const modifier = calculateModifier(props.stats?.dex);
								setResult({ ...result!, dice: "1d20", modifier: modifier });
							}}
						>
							<div className="monster__stat__name">DEX</div>
							<div className="monster__stat__value">
								{props.stats ? props.stats.dex : 0}
							</div>
						</Button>

						<Button
							variant="outlined"
							className="monster__stat"
							onClick={() => {
								const modifier = calculateModifier(props.stats?.con);
								setResult({ ...result!, dice: "1d20", modifier: modifier });
							}}
						>
							<div className="monster__stat__name">CON</div>
							<div className="monster__stat__value">
								{props.stats ? props.stats.con : 0}
							</div>
						</Button>

						<Button
							variant="outlined"
							className="monster__stat"
							onClick={() => {
								const modifier = calculateModifier(props.stats?.int);
								setResult({ ...result!, dice: "1d20", modifier: modifier });
							}}
						>
							<div className="monster__stat__name">INT</div>
							<div className="monster__stat__value">
								{props.stats ? props.stats.int : 0}
							</div>
						</Button>

						<Button
							variant="outlined"
							className="monster__stat"
							onClick={() => {
								const modifier = calculateModifier(props.stats?.wis);
								setResult({ ...result!, dice: "1d20", modifier: modifier });
							}}
						>
							<div className="monster__stat__name">WIS</div>
							<div className="monster__stat__value">
								{props.stats ? props.stats.wis : 0}
							</div>
						</Button>

						<Button
							variant="outlined"
							className="monster__stat"
							onClick={() => {
								const modifier = calculateModifier(props.stats?.cha);
								setResult({ ...result!, dice: "1d20", modifier: modifier });
							}}
						>
							<div className="monster__stat__name">CHA</div>
							<div className="monster__stat__value">
								{props.stats ? props.stats.cha : 0}
							</div>
						</Button>
					</div>

					{/* This is the HP section */}
					<div className="monster__hp__calc">
						<div className="monster__hp__name">maxHP</div>
						<div className="monster__stat__value">{props.hp}</div>
						<TextField
							label="Damage taken"
							variant="filled"
							color="warning"
							onChange={(e) => updateDamage(e.target.value)}
							size="small"
						/>
					</div>
				</div>
			</div>
			<div className="monster__actions">
				<div className="monster__actions__name">Actions</div>
				<div className="monster__actions__apa">
					Attacks per action: {props.actions?.apa ?? 0}
				</div>
				<div className="monster__actions__list">
					{props.actions?.map((action, key) => {
						return (
							<div className="monster__action" key={key}>
								<div className="monster__action__name">{action.name}</div>
								<div className="monster__action__description">
									{action.description}
								</div>
								<div className="monster__action__attack">
									<Button
										variant="outlined"
										size="small"
										onClick={() => {
											setResult({
												...result!,
												dice: "1d20",
												modifier: action.toHit,
											});
										}}
									>
										To hit
									</Button>
									<Button
										variant="outlined"
										size="small"
										onClick={() => {
											setResult({
												...result!,
												dice: action.damageDie,
												modifier: action.damageBonus,
											});
										}}
									>
										Attack
									</Button>
								</div>
							</div>
						);
					})}
				</div>
			</div>
		</div>
	);
};

export default Monster;
