import { useQuery } from '@apollo/client';
import React from 'react'
import { ListOfFriends, ThisUser } from '../api/queries';

type Friend = {
  id:number,
  username:string
  dateOfBirth:string
}

function FriendsSection({
  ID
}:{ID:number}) {
  const {loading, data, error} = useQuery(ListOfFriends(ID));
  return (
    <div>
      <h3>Friends ...</h3>
      <div className="friends__container">
        {
          loading ?
            <p>loading...</p>
            :
            error ?
              <p>ERROR</p>
              :
              data.friends.map((Nickname:string,user:Friend)=>{
                <div className="frineds__card">
                  <p className="name">
                  {
                    Nickname ? Nickname
                    :
                    friend.username
                  }
                  </p>
                  <p className="birthday">
                    {friend.dateOfBirth}
                  </p>
                
                </div>
              })
        }

      </div>

    </div>
  )
}

export default FriendsSection
