import React from "react";
import MainBody from "../../components/MainBody";
import { Container } from "@mui/material";
import Monster from "../../components/dnd/Monster";
import "./Encounters.css";
import { mockMonsters } from "../../mock/MockMonsters";
import { Context } from "../../assets/diceStore";

function Encounters() {
	const monsters = mockMonsters;
	const { result, rollDice } = React.useContext(Context);
	const [diceResult, setDiceResult] = React.useState(0);

	React.useEffect(() => {
		if (result) {
			setDiceResult(rollDice(result.dice, result.modifier));
		}
	}, [result, rollDice]);

	return (
		<MainBody>
			<Container maxWidth="lg">
				<div className="monsters_list">
					{monsters.map((monster, key) => {
						return <Monster {...monster} key={key} />;
					})}
				</div>
			</Container>
			<div className="diceResult">
				<div className="diceResult__title">Roll</div>
				<div className="diceResult__diceAndMod">
					{result?.dice} + {result?.modifier}
				</div>
				<div className="diceResult__results">{result?.result}</div>
			</div>
		</MainBody>
	);
}

export default Encounters;
