import React from 'react'
//import { useMyQueryQuery } from '../utils/__generated__/graphql'
import {Spinner} from "react-bootstrap";
import { useQuery} from '@apollo/client';
import MainPage from '../components/MainPage'
import FriendsSection from '../components/FriendsSection';
import {TestQuery,ThisUser} from '../api/queries';

let id = 3;
export const Page = () => {
  const {loading, data, error} = useQuery(ThisUser(id));

  return (
  <>
    {
      loading ?
      <Spinner animation="border"/> : !error ? 
      data.user.map(({Username}:{Username: string}) => (
        <section>
          <MainPage name ={Username}/>
          <FriendsSection ID={id}/>
        </section>
      )) : <h1> ERROR - MyQuery</h1>
    }
    <Spinner animation="grow" size="sm" />
  </>
  );
}
