import { gql} from '@apollo/client';

export const TestQuery = gql`
  query MyQuery {
    test {
      ID
      password
      username
    }
  }
`;

export function ThisUser({id}:{id:Number}){
  const ThisUserQuery = gql`
    query GetUser {
      user(where: {ID: {_eq: ${id}}}) {
        ID
        Username
        date_of_birth
        password
      }
    }
  `
  return ThisUserQuery;
}
