import React, { useMemo, useState } from "react";

export type ResultType = {
	dice: string;
	modifier: number;
	result: number;
};

const initialState = {
	dice: "1d20",
	modifier: 0,
	result: 0,
};

type DiceTContext = {
	result: ResultType | undefined;
	setResult: (
		newVal: ResultType | ((prevVal: ResultType) => ResultType)
	) => void;
	rollDice: (dice: string, modifier?: number) => ResultType;
};

export const Context = React.createContext<DiceTContext>({
	result: initialState,
	setResult: () => {},
	rollDice: () => initialState,
});

export function DiceProvider({ children }: { children: React.ReactNode }) {
	const [result, setResult] = useState<ResultType>(initialState);

	const value = React.useMemo(() => {
		const rolled = rollDice(result.dice, result.modifier);
		return { rolled, setResult, rollDice };
	}, [result, setResult]);

	return <Context.Provider value={value}>{children}</Context.Provider>;
}

export const rollDice = (dice: string, modifier = 0) => {
	const [numDice, die] = dice.split("d");

	let total = 0;
	for (let i = 0; i < parseInt(numDice); i++) {
		total += Math.floor(Math.random() * parseInt(die)) + 1;
	}
	return { dice: dice, modifier: modifier, result: total + modifier };
};
