import React from 'react';
import {ApolloClient, InMemoryCache, ApolloProvider,HttpLink, ApolloLink,concat} from '@apollo/client';

import './App.css';
import { Page } from './pages/page';


const httpLink = new HttpLink({uri: 'https://rapid-jawfish-26.hasura.app/v1/graphql'});

const authMiddleware = new ApolloLink((operation, forward) => {
  // add the authorization to the headers
  operation.setContext(({ headers = {} }) => ({
    headers: {
      ...headers,
      "x-hasura-admin-secret": "toJYeHHUbn2AoWAanM6duuJBmGcunDU3aLrT84ubUTOQXwu2TA2eZLEd5ZYaeflq" || null,
    }
  }));

  return forward(operation);
})

const client = new ApolloClient({
  cache: new InMemoryCache(),
  link: concat(authMiddleware, httpLink)
});

function App() {
  return (
    <div className="app">
      <ApolloProvider client={client}>
        <h1>poo</h1>
        <Page/>
      </ApolloProvider>
    </div>

  );
}

export default App;
